import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
function Characters() {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []); 
    const gryffindorStudents= charactersHP.filter(char=> char.house==='Gryffindor' && char.hogwartsStudent===true)
    const slytherinStudents= charactersHP.filter(char=> char.house==='Gryffindor'&& char.hogwartsStudent===true)
    const hufflepuffStudents= charactersHP.filter(char=> char.house==='Gryffindor'&& char.hogwartsStudent===true)
    const ravenclawStudents= charactersHP.filter(char=> char.house==='Gryffindor' && char.hogwartsStudent===true)

    return (
      <div>
        <h1>Students</h1>
        {charactersHP.map((character) => (
            <div key={character._id}>
                   <img src={character.image} alt={character.name}/>
          <h2 >{character.name}</h2>
          <p>{character.house}</p>
          <p>{character.patronous}</p>
          {character.alive? <h1>alive</h1>:<h2>RIP</h2>}
          </div>
        ))}
      </div>
    );
  }

  export default Characters
