import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import { Link } from 'react-router-dom'

const Ravenclaw = () => {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []); 

    const ravenclawStudents= charactersHP.filter(char=> char.house==='Ravenclaw' && char.hogwartsStudent===true)


    return (
            <div>
            <h2>Ravenclaw</h2>
            <img src={'https://i.pinimg.com/originals/b1/11/49/b1114929ca49434e0f67ae461753e7e7.jpg'} alt={'Gryffindor'}/>
            {ravenclawStudents.map((character) => (
                <Link to={`/admin/${character._id}`}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                </Link>
            ))}
        </div>

    )
}
export default Ravenclaw
