import styled from 'styled-components';

const Card = styled.div`
  box-sizing: border-box;
  max-width: 410px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;



const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Input = styled.input`
  padding: 1rem;
  border: 1px solid #999;
  margin-bottom: 1rem;
  font-size: 0.8rem;
`;

const Button = styled.button`
  background: linear-gradient(to bottom, #6371c7, #5563c1);
  border-color: #3f4eae;
  border-radius: 3px;
  padding: 1rem;
  color: white;
  font-weight: 700;
  width: 100%;
  margin-bottom: 1rem;
  font-size: 0.8rem;
`;

const Logo = styled.img`
  width: 50%;
  margin-bottom: 1rem;
`;

const Error = styled.div`
  background-color: red;
`;

export const Wrap = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: space-around;
`;

export const CardChar = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align:center;
`;
const CardInfo= styled.div`
  border-radius: 20px;
  box-shadow: 4px 8px 89px -12px rgba(118,118,118,0.22);
  width: 420px;
  height: 250px;
  margin: 10px;
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const InfoColumn= styled.div`
  display: flex;
  flex-direction: column;
`;

const ImgHouse = styled.img` 
    max-width: 210px;
    border-radius: 10px;
`;
const ImgChar = styled.img` 
    width: 170px;
    height: 200px;
    border-radius: 10px;
    margin: 30px;
`;

const LinkChar = styled(Link)` 
    text-decoration: none;

`;


export { Form, Input, Button, Logo, Card, Error };
