import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
function Characters() {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []); 
    const gryffindorStudents= charactersHP.filter(char=> char.house==='Gryffindor' && char.hogwartsStudent===true)
    const slytherinStudents= charactersHP.filter(char=> char.house==='Slytherin'&& char.hogwartsStudent===true)
    const hufflepuffStudents= charactersHP.filter(char=> char.house==='v'&& char.hogwartsStudent===true)
    const ravenclawStudents= charactersHP.filter(char=> char.house==='Ravenclaw' && char.hogwartsStudent===true)

    return (
      <div>
      <div>
            <h1>Students</h1>
            <h2>Gryffindor</h2>
            {gryffindorStudents.map((character) => (
                <div key={character._id}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                    <p>{character.patronous}</p>
                    {character.alive? <h1>alive</h1>:<h2>RIP</h2>}
                </div>
            ))}
        </div>
        <div>
            <h1>Students</h1>
            <h2>Slytherin</h2>
            {slytherinStudents.map((character) => (
                <div key={character._id}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                    <p>{character.patronous}</p>
                    {character.alive? <h1>alive</h1>:<h2>RIP</h2>}
                </div>
            ))}
        </div>
        <div>
            <h1>Students</h1>
            <h2>Hufflepuff</h2>
            {hufflepuffStudents.map((character) => (
                <div key={character._id}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                    <p>{character.patronous}</p>
                    {character.alive? <h1>alive</h1>:<h2>RIP</h2>}
                </div>
            ))}
        </div>
        <div>
            <h1>Students</h1>
            <h2>Ravenclaw</h2>
            {ravenclawStudents.map((character) => (
                <div key={character._id}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                    <p>{character.patronous}</p>
                    {character.alive? <h1>alive</h1>:<h2>RIP</h2>}
                </div>
            ))}
        </div>
      </div>
    );
  }

  export default Characters
