import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import {CardProfile, InfoColumn, ImgChar, LinkChar, InfoRow} from '../components/AuthForms'


const CharProfile = (props) => {
    const name= props.match.params.name
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []);

    const student= charactersHP.filter(char=> char.name===name)

    console.log(student)
    return (
        <div>
          {student.map((character) => (
                <LinkChar key={character.name} to={`/char/${character.name}` }>
                    <CardProfile >
                    <ImgChar src={character.image} alt={character.name}/>
                        <InfoRow>
                        <div>
                            <h2> {character.name}</h2>
                            <p>House: {character.house}</p>
                            <p>Birthday: {character.dateOfBirth}</p>
                            {character.alive===true? <h2> Alive</h2>: <h2>Dead</h2>}
                        </div>
                        <div>
                            <h3>Wand characteristics</h3>
                            <p>Core:{character.wand.core}</p>
                            <p>Length: {character.wand.length}</p>
                            <p>Wood: {character.wand.wood}</p>

                            <p>Patronous: {character.patronus}</p>
                            <p>Species: {character.species}</p>
                        </div>
                        </InfoRow>
                    </CardProfile>

                </LinkChar>
            ))}
        </div>
    )
}
export default CharProfile
