import React from "react";
import { Button } from "../components/AuthForms";
import { useAuth } from "../context/auth";
import Gryffindor from '../components/Gryffindor';
import Slytherin from '../components/Slytherin';
import Hufflepuff from '../components/Hufflepuff';
import Ravenclaw from '../components/Ravenclaw';
import Staff from '../components/Staff';
import { CardChar, Title} from '../components/AuthForms'



function Admin(value) {
  const { setAuthTokens, authTokens } = useAuth();

  function logOut() {
    setAuthTokens();
  }

  return (
    <CardChar>
      <Button onClick={logOut}>Log out</Button>
        <Title>
      {authTokens.staff===true? <h1> Staff's Page</h1>: <h1> Student's Page</h1>}
            <h1>WELCOME {authTokens.name.toUpperCase()}</h1>
            <h3>You have access to all the information you requiere.
            To logout say "mischief managed"... or click the Log out button.</h3>
        </Title>
        <h1>Students</h1> 
            <Gryffindor/>
            <Slytherin/>
            <Hufflepuff/>
            <Ravenclaw/>
        <h1>Staff</h1> 
            <Staff/>
    </CardChar>
  );
}

export default Admin;
