import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import {CardProfile, Status, ImgChar, LinkChar, InfoRow, DivSpace} from '../components/AuthForms'


const CharProfile = (props) => {
    const name= props.match.params.name
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []);

    const student= charactersHP.filter(char=> char.name===name)

    return (
        <div>
          {student.map((character) => (
                <div key={character.name}>
                    <CardProfile >
                        <div>
                            <ImgChar src={character.image} alt={character.name}/>
                            <h3> {character.name}</h3>
                        </div>
                        <InfoRow>
                        <DivSpace>
                            <h3> General information</h3>
                            <p><b>House:</b> {character.house}</p>
                            <p><b>Birthday:</b> {character.dateOfBirth}</p>
                            <p><b>Patronous:</b> {character.patronus}</p>
                            <p><b>Species:</b> {character.species}</p>
                        </DivSpace>
                        <DivSpace>
                            <h3>Wand information</h3>
                            <p><b>Core:</b> {character.wand.core}</p>
                            <p><b>Length:</b> {character.wand.length}</p>
                            <p><b>Wood:</b> {character.wand.wood}</p>

                        </DivSpace>
                        </InfoRow>
                        {character.alive===true? <Status src={'https://upload.wikimedia.org/wikipedia/commons/thumb/f/f1/Heart_coraz贸n.svg/160px-Heart_coraz贸n.svg.png'}/>: <Status src={'https://i.pinimg.com/originals/9b/fb/f4/9bfbf45b1cb45e67d3e660de982ea9c0.png'}/>}

                    </CardProfile>

                </div>
            ))}
        </div>
    )
}
export default CharProfile
