import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import {Wrap, CardChar, CardInfo, InfoColumn, ImgHouse, ImgChar, LinkChar} from './AuthForms'


const Hufflepuff = () => {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []); 

    const hufflepuffStudents= charactersHP.filter(char=> char.house==='Hufflepuff'&& char.hogwartsStudent===true)

    return (
        <CardChar>
            <ImgHouse src={'https://images-na.ssl-images-amazon.com/images/I/61ANAw11SFL._AC_SY679_.jpg'} alt={'Hufflepuff'}/>
            <h1>Hufflepuff</h1>
            <Wrap>
            {hufflepuffStudents.map((character) => (
                <LinkChar to={`/char/${character.name}`}>
                    <CardInfo key={'298766876090090123'}>
                    <ImgChar src={character.image} alt={character.name}/>
                        <InfoColumn>
                            <h2 >{character.name}</h2>
                            <p>{character.house}</p>
                        </InfoColumn>
                    </CardInfo>
                </LinkChar>
            ))}
            </Wrap>
        </CardChar>
    )
}
export default Hufflepuff
