import styled from 'styled-components';
import { Link } from 'react-router-dom'


const Card = styled.div`
  box-sizing: border-box;
  max-width: 410px;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #F0F0F0;
  margin: 20vh auto;
  background: rgba( 255, 255, 255, 0.35 );
  box-shadow: 0 8px 32px 0 rgba( 31, 38, 135, 0.37 );
  backdrop-filter: blur( 6.0px );
  -webkit-backdrop-filter: blur( 6.0px );
  border-radius: 10px;
`;

const Title = styled.div`
  box-sizing: border-box;
  max-width: 70%;
  padding: 2rem;
  background-color: white;
  margin: 5vh auto;
  border-radius: 10px;
  box-shadow: 0 8px 32px 0 rgba( 128,128,128, 0.37 );

`;

const HP = styled.div`
  background-image: url('https://i.pinimg.com/originals/46/6b/6d/466b6d5e575a9d8eabbc97ce34d07993.png');
  height: 100vh;
  width: 100%;
  background-repeat: no-repeat;
  background-size: cover;
`;
const HPLogin = styled.div`
  background-image: url('https://wallpaper.dog/large/5862.jpg');
  height: 100vh;
  width: 100%;
  position: absolute;
  background-repeat: no-repeat;
  background-size: cover;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Input = styled.input`
  padding: 1rem;
  border: none;
  margin-bottom: 1rem;
  font-size: 0.8rem;
`;


const Button = styled.button`
  background: linear-gradient(to bottom, #6371c7, #5563c1);
  border: none;
  border-radius: 3px;
  padding: 1rem;
  color: white;
  font-weight: 700;
  width: 100%;
  margin-bottom: 1rem;
  font-size: 0.8rem;
`;

const ButtonHome = styled(Link)`
  background: #FFD300;
  border: none;
  text-decoration: none;
  border-radius: 3px;
  padding: 20px 60px;
  color: black;
  font-weight: 700;
  position:relative;
  top: 75vh;
  left: 44%;
  width: 200px;
  height: 100px;
  font-size: 0.8rem;
`;

const Logo = styled.img`
  width: 50%;
  margin-bottom: 1rem;
`;

const Status = styled.img`
  width: 100px;
  margin-bottom: 20px; 
`;

const Error = styled.div`
  background-color: #E65843;
  padding: 10px;
  font-size: 14px;
  color: white;
`;

const Wrap = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: space-around;
  margin: 30px
`;

const CardChar = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align:center;
`;
const CardInfo= styled.div`
  border-radius: 20px;
  background-color: white;
  box-shadow: 4px 8px 89px -12px rgba(118,118,118,0.22);
  width: 420px;
  height: 250px;
  margin: 10px;
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const CardProfile= styled.div`
  border-radius: 20px;
  background-color: white;
  width: 1000px;
  box-shadow: 4px 8px 89px -12px rgba(118,118,118,0.22);
  margin: 15%;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;


const InfoColumn= styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  color: black;
`;
const InfoRow= styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  color: black;
`;

const DivSpace= styled.div`
  margin: 40px;
`;

const ImgHouse = styled.img` 
    width: 80px;
`;
const ImgChar = styled.img` 
    width: 170px;
    height: 200px;
    border-radius: 10px;
    margin: 30px;
`;

const LinkChar = styled(Link)` 
    text-decoration: none;

`;


export {HP, Title, HPLogin, ButtonHome, Form, Input, Button, Logo, Card, Error, Wrap, CardChar, CardInfo, InfoColumn, ImgHouse, ImgChar, LinkChar, CardProfile,InfoRow, DivSpace, Status };
