import { signupFn } from '../services/auth'
import { Link } from 'react-router-dom'
import { logoutFn } from '../services/auth'
import { useContextInfo } from '../hooks/context'
import {
  UserOutlined,
  BarChartOutlined
} from '@ant-design/icons';

const { Header} = Layout;
const { SubMenu } = Menu;
const { Title } = Typography
