import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import { Link } from 'react-router-dom'
import styled from 'styled-components';

const Wrap = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: space-around;
`;

const CardChar = styled.div`

  display: flex;
  flex-direction: column;
  align-items: center;
  text-align:center;
`;
const CardInfo= styled.div`
  border-radius: 20px;
  box-shadow: 4px 8px 89px -12px rgba(118,118,118,0.22);
  width: 420px;
  height: 250px;
  margin: 10px;
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const InfoColumn= styled.div`
  display: flex;
  flex-direction: column;
`;
const ImgHouse = styled.img` 
    max-width: 210px;
    border-radius: 10px;
`;
const ImgChar = styled.img` 
    width: 170px;
    height: 200px;
    border-radius: 10px;
    margin: 30px;
`;


const LinkChar = styled(Link)` 
    text-decoration: none;

`;

const Gryffindor = () => {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []);

    const gryffindorStudents= charactersHP.filter(char=> char.house==='Gryffindor' && char.hogwartsStudent===true)

    return (
        <CardChar>
            <h2>Gryffindor</h2>
            <ImgHouse src={'https://i.pinimg.com/originals/93/85/bf/9385bf3ca546d3c750363a78a68e0c70.jpg'} alt={'Gryffindor'}/>
            <Wrap>
            {gryffindorStudents.map((character) => (
                <LinkChar to={`/admin/${character._id}`}>
                    <CardInfo>
                    <ImgChar src={character.image} alt={character.name}/>
                        <InfoColumn>
                            <h2 >{character.name}</h2>
                            <p>{character.house}</p>
                        </InfoColumn>
                    </CardInfo>

                </LinkChar>
            ))}
            </Wrap>
        </CardChar>
    )
}
export default Gryffindor
