import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import axios from 'axios';
import { Card, Logo, Form, Input, Button, Error } from "../components/AuthForms";
import { useAuth } from "../context/auth";

function Login(props) {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isError, setIsError] = useState(false);
  const [user, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const { setAuthTokens } = useAuth();
  const [admin, setAdmin] = useState(true);

  let axiosConfig = {
    headers: {
      'Content-Type': 'text/plain',
      'Access-Control-Allow-Origin' : '*',
      'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE'
    }
  };

  const info ={
    user:user,
    password: password
  }

  function postLogin() {
    axios({url:"http://ec2-52-37-61-68.us-west-2.compute.amazonaws.com:1234/api/v1/challenge/login", 
    method: 'POST',  
    data: info
  }).then(result => {
      if (result.status === 200 && result.data.staff===true ) {
        setAuthTokens(result.data);
        setLoggedIn(true);
      } else if(  result.data.staff===false ){
        setAuthTokens(result.data);
        setLoggedIn(true);
        setAdmin(false);
      }else {
        setIsError(true);
      }
    }).catch(e => {
      setIsError(true);
    });
  }
  if (isLoggedIn && admin) {
    console.log('admin');
    return <Redirect to='/admin' />;
  }else if (isLoggedIn && !admin) {
    console.log('student');
    return <Redirect to='/student' />;
  }

  return (
    <Card>
      <Logo src={'https://i.pinimg.com/originals/1e/39/03/1e390396d7381a36a73407df5cc02fb1.png'} />
      <Form>
        <Input
          type="user"
          value={user}
          onChange={ e => {
            setUserName(e.target.value);
          }}
          placeholder="user"
        />
        <Input
          type="password"
          value={password}
          onChange={e => {
            setPassword(e.target.value);
          }}
          placeholder="password"
        />
        <Button onClick={postLogin}>Login</Button>
      </Form>
        { isError &&<Error>The username or password provided were incorrect!</Error> }
    </Card>
  );
}

export default Login;
