import React, { useEffect, useState } from 'react';
import getChar from '../services/getChar'
import { Link } from 'react-router-dom'

export const Slytherin = () => {
    const [charactersHP, setCharacters] = useState([]);

    useEffect(() => {  
      getChar()
        .then((characters) =>setCharacters(characters))
        .catch((error) => console.error(error));
    }, []); 


    const slytherinStudents= charactersHP.filter(char=> char.house==='Slytherin'&& char.hogwartsStudent===true)

    return (
        <div>
            <img src={'https://miro.medium.com/max/500/0*m0pYj25tg4A2mAfU.png'} alt={'Slytherin'}/>
            <h2>Slytherin</h2>
            {slytherinStudents.map((character) => (
                <Link to={`/admin/${character._id}`}>
                    <img src={character.image} alt={character.name}/>
                    <h2 >{character.name}</h2>
                    <p>{character.house}</p>
                </Link>
            ))}
        </div>
    )
}
export default Slytherin
